mapper.select:

SELECT C.*, REPLACE(CONVERT(C.NUM_DESC, CHAR),'.0','') AS NUM_DESC_ORDER FROM (
  SELECT B.*, (@rownumB:=@rownumB+1) as NUM_ASC FROM(  
    SELECT A.*, (@rownumA:=@rownumA+1) as NUM_DESC FROM (

			    SELECT
			    	PPQD.DATA_IDX,
				 	PPQ.QUES_IDX,
				 	PPQ.QUES_CNTN,
				 	PPQD.DATA_CNTN
				FROM 
					PPM_PRDT_QUES PPQ,
					PPM_PRDT_QUES_DATA PPQD
				WHERE
					QUES_USE_YN = 'Y'
					AND PPQ.QUES_IDX = PPQD.QUES_IDX
					AND PPQD.BUY_IDX = #{buyIdx}




						AND CHAT_IDX = #{prdtIdx}




						AND FACE_IDX = #{prdtIdx}




						AND LIVE_IDX = #{prdtIdx}





	) A JOIN (SELECT @rownumA:=0) rownumA
  ) B JOIN (SELECT @rownumB:=0) rownumB ORDER BY DATA_IDX
) C WHERE NUM_ASC BETWEEN #{startRow } AND '9999'

SELECT C.*, REPLACE(CONVERT(C.NUM_DESC, CHAR),'.0','') AS NUM_DESC_ORDER FROM ( SELECT B.*, (@rownumB:=@rownumB+1) as NUM_ASC FROM( SELECT A.*, (@rownumA:=@rownumA+1) as NUM_DESC FROM ( SELECT PPV.VOD_IDX, PU.USER_NM, PPLE.LECT_NM, PPV.VOD_NM, PPVD.DATA_TITL, PPVD.DATA_CD, ( SELECT COUNT(0) FROM PPM_PRDT_VOD_DATA WHERE VOD_IDX = PPV.VOD_IDX ) AS VOD_TOTAL FROM PPM_USER_BUY_PROG PUBP, PPM_USER_BUY PUB, PPM_PRDT_LECT PPLE, PPM_PRDT_LECT_DATA PPLD, PPM_PRDT_VOD PPV, PPM_PRDT_VOD_DATA PPVD, PPM_USER PU, PPM_CATE PC WHERE PUBP.BUY_IDX = PUB.BUY_IDX AND PUBP.DATA_IDX = PPVD.DATA_IDX AND PUB.CATE_IDX = PC.CATE_IDX AND PUB.BUY_PRDT_CD = PPLE.LECT_CD AND PPLE.LECT_IDX = PPLD.LECT_IDX AND PPLD.VOD_IDX = PPVD.VOD_IDX AND PPLD.VOD_IDX = PPV.VOD_IDX AND PC.USER_IDX = PU.USER_IDX
AND PUB.BUY_IDX = #{buyIdx} AND PUB.BUY_PRDT_CD = #{prdtCd}
) A JOIN (SELECT @rownumA:=0) rownumA ) B JOIN (SELECT @rownumB:=0) rownumB ORDER BY VOD_IDX DESC ) C WHERE NUM_ASC BETWEEN #{startRow } AND '9999'
